const e=JSON.parse('{"key":"v-d80a9d56","path":"/%E5%90%8E%E7%AB%AF%E6%8A%80%E6%9C%AF/%E6%A1%86%E6%9E%B6%E7%AF%87%E9%9D%A2%E8%AF%95%E9%A2%98-%E5%8F%82%E8%80%83%E5%9B%9E%E7%AD%94.html","title":"框架篇面试题-参考回答","lang":"zh-CN","frontmatter":{"date":"2023-12-15T00:00:00.000Z","category":["Spring","SpringMVC","MyBatis","SpringBoot","后端技术"],"description":"框架篇面试题-参考回答 知识网络架构 知识框架 Spring 面试官：Spring框架中的单例bean是线程安全的吗？ Spring框架中的Bean是单例的吗？ Spring框架中的Bean是单例的，在注册Bean时，可以通过使用Scope注解声明当前的Bean是singleton或者prototype。如果不显示声明，默认为单例。 如何判断Spring开发中的线程安全问题？ Controller方法中接收的参数是局部变量，一般没有线程安全问题；但是如果在Controller类中定义了全局变量，并且这个全局变量是可修改的，则这个变量就有线程安全问题。如果这个全局变量没有状态（不可被修改，例如注入的Service对象、dao对象），则这个全局变量就是线程安全的。 所以在日常开发过程中应该注意，在Bean中尽量避免定义可被修改的全局成员变量，因为并不能保证他的线程安全。","head":[["meta",{"property":"og:url","content":"https://example.com/%E5%90%8E%E7%AB%AF%E6%8A%80%E6%9C%AF/%E6%A1%86%E6%9E%B6%E7%AF%87%E9%9D%A2%E8%AF%95%E9%A2%98-%E5%8F%82%E8%80%83%E5%9B%9E%E7%AD%94.html"}],["meta",{"property":"og:site_name","content":"TOMMY"}],["meta",{"property":"og:title","content":"框架篇面试题-参考回答"}],["meta",{"property":"og:description","content":"框架篇面试题-参考回答 知识网络架构 知识框架 Spring 面试官：Spring框架中的单例bean是线程安全的吗？ Spring框架中的Bean是单例的吗？ Spring框架中的Bean是单例的，在注册Bean时，可以通过使用Scope注解声明当前的Bean是singleton或者prototype。如果不显示声明，默认为单例。 如何判断Spring开发中的线程安全问题？ Controller方法中接收的参数是局部变量，一般没有线程安全问题；但是如果在Controller类中定义了全局变量，并且这个全局变量是可修改的，则这个变量就有线程安全问题。如果这个全局变量没有状态（不可被修改，例如注入的Service对象、dao对象），则这个全局变量就是线程安全的。 所以在日常开发过程中应该注意，在Bean中尽量避免定义可被修改的全局成员变量，因为并不能保证他的线程安全。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2025-09-25T07:09:52.000Z"}],["meta",{"property":"article:author","content":"tommy"}],["meta",{"property":"article:published_time","content":"2023-12-15T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2025-09-25T07:09:52.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"框架篇面试题-参考回答\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2023-12-15T00:00:00.000Z\\",\\"dateModified\\":\\"2025-09-25T07:09:52.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"tommy\\",\\"url\\":\\"https://devtommy.github.io\\"}]}"]]},"headers":[{"level":2,"title":"知识网络架构","slug":"知识网络架构","link":"#知识网络架构","children":[]},{"level":2,"title":"Spring","slug":"spring","link":"#spring","children":[]},{"level":2,"title":"SpringMVC","slug":"springmvc","link":"#springmvc","children":[]},{"level":2,"title":"SpringBoot","slug":"springboot","link":"#springboot","children":[]},{"level":2,"title":"MyBatis","slug":"mybatis","link":"#mybatis","children":[]}],"git":{"createdTime":1758784192000,"updatedTime":1758784192000,"contributors":[{"name":"tommy","email":"9157365+zza20020321@user.noreply.gitee.com","commits":1}]},"readingTime":{"minutes":14.72,"words":4417},"filePathRelative":"后端技术/框架篇面试题-参考回答.md","localizedDate":"2023年12月15日","excerpt":"<h1> 框架篇面试题-参考回答</h1>\\n<h2> 知识网络架构</h2>\\n<figure><img src=\\"https://raw.githubusercontent.com/devtommy2/PicBed/main/1702482846015.png\\" alt=\\"知识框架\\" tabindex=\\"0\\" loading=\\"lazy\\"><figcaption>知识框架</figcaption></figure>\\n<h2> Spring</h2>\\n<p><strong>面试官</strong>：Spring框架中的单例bean是线程安全的吗？</p>\\n<blockquote>\\n<p><strong>Spring框架中的Bean是单例的吗？</strong></p>\\n<p>Spring框架中的Bean是单例的，在注册Bean时，可以通过使用Scope注解声明当前的Bean是singleton或者prototype。如果不显示声明，默认为单例。</p>\\n<p><strong>如何判断Spring开发中的线程安全问题？</strong></p>\\n<p>Controller方法中接收的参数是局部变量，一般没有线程安全问题；但是如果在Controller类中定义了<strong>全局变量</strong>，并且这个全局变量是可修改的，则这个变量就有线程安全问题。如果这个<strong>全局变量</strong>没有状态（不可被修改，例如注入的Service对象、dao对象），则这个全局变量就是线程安全的。</p>\\n<p>所以在日常开发过程中应该注意，在Bean中尽量避免定义可被修改的全局成员变量，因为并不能保证他的线程安全。</p>\\n</blockquote>","autoDesc":true}');export{e as data};
